mapper.select:
SELECT ID, PROGRAM_NAME, CREATED_DATE, (CEIL(TO_DATE(PROGRAM_END_DATE)-SYSDATE)) PROGRAM_END_DATE, PROGRAM_EXPIRED
FROM TBL_PROGRAM
WHERE COMPANY_ID = #{companyId}

SELECT COUNT(*)
FROM TBL_PROGRAM
WHERE COMPANY_ID = #{companyId}

SELECT ID, COMPANY_NAME, PROGRAM_NAME, TO_CHAR(CREATED_DATE, 'YYYY-MM-DD') AS CREATED_DATE , TO_CHAR(PROGRAM_END_DATE, 'YYYY-MM-DD') AS PROGRAM_END_DATE, PROGRAM_STATUS, PROGRAM_THUMBNAIL_PATH, COMPANY_INTRODUCE, PROGRAM_BENEFIT, PROGRAM_DETAIL FROM ( SELECT ID, ROWNUM R, COMPANY_NAME, PROGRAM_NAME, CREATED_DATE, PROGRAM_END_DATE, PROGRAM_STATUS, PROGRAM_THUMBNAIL_PATH, COMPANY_INTRODUCE, PROGRAM_BENEFIT, PROGRAM_DETAIL FROM ( SELECT P.ID, COMPANY_NAME, PROGRAM_NAME, P.CREATED_DATE, PROGRAM_END_DATE, P.PROGRAM_STATUS, P.PROGRAM_THUMBNAIL_PATH, C.COMPANY_INTRODUCE, PROGRAM_BENEFIT, PROGRAM_DETAIL FROM TBL_PROGRAM P JOIN TBL_COMPANY C ON P.COMPANY_ID = C.ID
AND P.PROGRAM_STATUS = #{status}
AND PROGRAM_NAME LIKE '%' || #{keyword} || '%'
AND CEIL(SYSDATE - TO_DATE(P.CREATED_DATE)) <= #{date}
ORDER BY P.ID DESC )
WHERE ROWNUM <= ${endRow}
)
WHERE R >= ${startRow}

mapper.update:
UPDATE TBL_PROGRAM
SET PROGRAM_STATUS = #{programStatus}
WHERE ID = #{id}

mapper.select:
SELECT COUNT(*) AS TOTAL FROM ( SELECT P.ID, COMPANY_NAME, PROGRAM_NAME, P.CREATED_DATE, PROGRAM_END_DATE, P.PROGRAM_STATUS FROM TBL_PROGRAM P JOIN TBL_COMPANY C ON P.COMPANY_ID = C.ID
AND P.PROGRAM_STATUS = #{status}
AND PROGRAM_NAME LIKE '%' || #{keyword} || '%'
AND CEIL(SYSDATE - TO_DATE(P.CREATED_DATE)) <= #{date}
)

SELECT PROGRAM_NAME, programEndDate, COMPANY_NAME FROM ( SELECT ROWNUM R, PROGRAM_NAME, programEndDate, COMPANY_NAME FROM ( SELECT P.PROGRAM_NAME, CEIL(PROGRAM_END_DATE - SYSDATE) AS programEndDate, C.COMPANY_NAME, A.APPLY_MEMBER_STATUS FROM TBL_COMPANY C JOIN TBL_PROGRAM P ON C.ID = P.COMPANY_ID JOIN TBL_APPLY A ON A.PROGRAM_ID = P.ID AND A.MEMBER_ID = #{id} AND A.APPLY_MEMBER_STATUS = #{applyMemberStatus} ORDER BY A.ID DESC )
WHERE ROWNUM <= #{pagination.endRow}
)
WHERE R >= #{pagination.startRow}

SELECT COUNT(*)
FROM TBL_MEMBER M JOIN TBL_APPLY A
ON M.ID = A.MEMBER_ID AND A.MEMBER_ID = #{id}

SELECT APPLY_MEMBER_STATUS
FROM TBL_APPLY
WHERE MEMBER_ID = #{id}
