mapper.select:
SELECT ID, COMPANY_INQUIRY_TYPE, COMPANY_NAME, MEMBER_NAME, COMPANY_INQUIRY_DETAIL, TO_CHAR(CREATED_DATE, 'YYYY-MM-DD') AS CREATED_DATE, COMPANY_INQUIRY_STATUS FROM ( SELECT ID, ROWNUM R, COMPANY_INQUIRY_TYPE, COMPANY_NAME, COMPANY_INQUIRY_DETAIL, MEMBER_NAME, CREATED_DATE, COMPANY_INQUIRY_STATUS FROM ( SELECT CI.ID, COMPANY_INQUIRY_TYPE, C.COMPANY_NAME, COMPANY_INQUIRY_DETAIL, M.MEMBER_NAME, CI.CREATED_DATE, COMPANY_INQUIRY_STATUS FROM TBL_COMPANY_INQUIRY CI JOIN TBL_COMPANY_MEMBER CM ON CI.MEMBER_ID = CM.ID JOIN TBL_MEMBER M ON CM.ID = M.ID JOIN TBL_COMPANY C ON CM.COMPANY_ID = C.ID
AND CI.COMPANY_INQUIRY_STATUS = #{status}
AND MEMBER_NAME LIKE '%' || #{companyInquiryKeyword} || '%'
AND CEIL(SYSDATE - TO_DATE(CI.CREATED_DATE)) <= #{date}
ORDER BY CI.ID DESC )
WHERE ROWNUM <= ${endRow}
)
WHERE R >= ${startRow}

SELECT COUNT(*) FROM ( SELECT CI.ID, COMPANY_INQUIRY_TYPE, C.COMPANY_NAME, M.MEMBER_NAME, CI.CREATED_DATE, COMPANY_INQUIRY_STATUS FROM TBL_COMPANY_INQUIRY CI JOIN TBL_COMPANY_MEMBER CM ON CI.MEMBER_ID = CM.ID JOIN TBL_MEMBER M ON CM.ID = M.ID JOIN TBL_COMPANY C ON CM.COMPANY_ID = C.ID
AND CI.COMPANY_INQUIRY_STATUS = #{status}
AND MEMBER_NAME LIKE '%' || #{companyInquiryKeyword} || '%'
AND CEIL(SYSDATE - TO_DATE(M.MEMBER_RECENT_LOGIN)) <= #{date}
)

mapper.update:
UPDATE TBL_COMPANY_INQUIRY
SET COMPANY_INQUIRY_STATUS = #{companyInquiryStatus}, UPDATED_DATE = SYSDATE
WHERE ID = #{id}
